CREATE TABLE "customer_levels" ("id" VARCHAR PRIMARY KEY  NOT NULL , "level" VARCHAR NOT NULL , "pricelevel" INTEGER, "discount" FLOAT, "created" INTEGER, "modified" INTEGER);
CREATE TABLE "customer_types" ("id" VARCHAR PRIMARY KEY  NOT NULL , "type" VARCHAR NOT NULL , "created" INTEGER, "modified" INTEGER);
CREATE TABLE "customers" ("id" VARCHAR NOT NULL ,"customer_id" VARCHAR NOT NULL ,"name" VARCHAR,"telephone" VARCHAR,"dob" INTEGER,"sex" VARCHAR,"marital_status" VARCHAR,"company" VARCHAR,"title" VARCHAR,"email" VARCHAR,"addr1_tel1" VARCHAR,"addr1_tel2" VARCHAR,"addr1_fax" VARCHAR,"addr1_addr1" VARCHAR,"addr1_addr2" VARCHAR,"addr1_zip" VARCHAR,"addr1_city" VARCHAR,"addr1_county" VARCHAR,"addr1_state" VARCHAR,"addr2_tel1" VARCHAR,"addr2_tel2" VARCHAR,"addr2_fax" VARCHAR,"addr2_addr1" VARCHAR,"addr2_addr2" VARCHAR,"addr2_zip" VARCHAR,"addr2_city" VARCHAR,"addr2_state" VARCHAR,"addr2_county" VARCHAR,"level" VARCHAR,"created" INTEGER,"modified" INTEGER,"active" BOOL,"notes1" VARCHAR,"notes2" VARCHAR,"mobile" VARCHAR,"type" VARCHAR,"notes" VARCHAR,"location" VARCHAR,"created_by" VARCHAR,"created_at" VARCHAR, PRIMARY KEY ("id","customer_id") );
