CREATE TABLE "syncs" ("id" INTEGER PRIMARY KEY NOT NULL ,"crud" varchar(255) NOT NULL ,"machine_id" varchar(36) ,"from_machine_id" varchar(36) ,"method_id" varchar(36) NOT NULL ,"method_type" varchar(45) NOT NULL ,"method_table" varchar(45) NOT NULL ,"created" int NOT NULL ,"modified" int NOT NULL);
CREATE TABLE "takeout_status" ("id" VARCHAR PRIMARY KEY  NOT NULL ,"sequence" VARCHAR,"table_no" VARCHAR,"takeout_status" VARCHAR,"delivery_staff_displayname" VARCHAR,"member" VARCHAR,"telephone" VARCHAR,"address" TEXT,"member_displayname" VARCHAR,"created" INTEGER,"modified" INTEGER,"order_id" VARCHAR,"completed" BOOLEAN, "recallable" BOOLEAN, "destination" VARCHAR, "member_id" VARCHAR);
